// frontend/src/components/ForecastForm.js
import React, { useState } from 'react';

function ForecastForm({ onGetForecast, onLocationSelect }) {
  const [cityName, setCityName] = useState('');
  const [customization, setCustomization] = useState('');
  const [forecastDate, setForecastDate] = useState('');

  // Geocode a place name using the Nominatim API
  const geocodePlace = async () => {
    if (!cityName) {
      alert("Please enter a place name.");
      return;
    }

    try {
      const geocodeUrl = `https://nominatim.openstreetmap.org/search?q=${cityName}&format=json&limit=1`;
      const response = await fetch(geocodeUrl);
      const data = await response.json();

      if (data.length > 0) {
        const location = data[0];
        // Update parent with new lat/lon
        onLocationSelect(parseFloat(location.lat), parseFloat(location.lon));
      } else {
        alert("Location not found.");
      }
    } catch (err) {
      console.error("Geocoding error:", err);
      alert("Error geocoding location.");
    }
  };

  // Handler for the "Submit" button
  const handleSubmit = () => {
    onGetForecast(customization, forecastDate);
  };

  return (
    <div className="forecast-form">
      <h2>Get Long-Range Forecast</h2>

      <label htmlFor="cityName">Enter City Name:</label>
      <input
        id="cityName"
        type="text"
        value={cityName}
        onChange={(e) => setCityName(e.target.value)}
      />
      <button onClick={geocodePlace}>Find Location</button>

      <label htmlFor="forecastDate">Specific Date:</label>
      <input
        id="forecastDate"
        type="date"
        value={forecastDate}
        onChange={(e) => setForecastDate(e.target.value)}
      />

      <label htmlFor="customization">Customize Forecast:</label>
      <input
        id="customization"
        type="text"
        placeholder="Temperature, Rain Probability, etc."
        value={customization}
        onChange={(e) => setCustomization(e.target.value)}
      />

      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default ForecastForm;
