// frontend/src/App.js
import React, { useState } from 'react';
import './App.css';

import NavBar from './components/NavBar';
import Footer from './components/Footer';
import MapComponent from './components/MapComponent';
import ForecastForm from './components/ForecastForm';
import ForecastResult from './components/ForecastResult';

// Utility: convert a date (e.g. "2025-04-10") to the day of the year (1..365)
function dateToDayOfYear(dateString) {
  const dateObj = new Date(dateString);
  const start = new Date(dateObj.getFullYear(), 0, 0);
  const diff = dateObj - start;
  const oneDay = 1000 * 60 * 60 * 24;
  return Math.floor(diff / oneDay);
}

function App() {
  const [selectedLocation, setSelectedLocation] = useState({ lat: null, lon: null });
  const [forecastData, setForecastData] = useState(null);

  // Called when user selects a point on the map
  const handleLocationSelect = (lat, lon) => {
    setSelectedLocation({ lat, lon });
  };

  /**
   * Called by ForecastForm:
   * params = {
   *   forecastDate: <string YYYY-MM-DD>,
   *   forecastRange: 1 or 7 or 14 or 30,
   *   temperature: boolean,
   *   wind: boolean,
   *   rain: boolean,
   *   moisture: boolean
   * }
   */
  const getForecast = async (params) => {
    const { forecastDate, forecastRange, temperature, wind, rain, moisture } = params;

    if (!selectedLocation.lat || !selectedLocation.lon) {
      alert("Please select a location on the map or via city name first.");
      return;
    }

    // Convert date to day-of-year
    const dayOfYear = dateToDayOfYear(forecastDate);

    // Build query parameters
    const queryParams = new URLSearchParams({
      lat: selectedLocation.lat,
      lon: selectedLocation.lon,
      startDay: dayOfYear,
      range: forecastRange,
      temp: temperature ? '1' : '0',
      wind: wind ? '1' : '0',
      rain: rain ? '1' : '0',
      moisture: moisture ? '1' : '0',
    });

    try {
      const response = await fetch(`http://127.0.0.1:8000/api/weather?${queryParams.toString()}`);
      const data = await response.json();

      if (data.error) {
        alert(data.error);
      } else {
        // Attach the chosen date for display in ForecastResult
        data.chosenDate = forecastDate;
        setForecastData(data);
      }
    } catch (err) {
      console.error('Error fetching data from backend:', err);
      alert('Error fetching data from backend.');
    }
  };

  return (
    <div className="app-container">
      {/* NAV BAR (Dark-themed) */}
      <NavBar />

      <div className="container my-4">
        {/* Forecast form card */}
        <ForecastForm
          onGetForecast={getForecast}
          onLocationSelect={handleLocationSelect}
        />

        {/* Map section card */}
        <MapComponent
          onLocationSelect={handleLocationSelect}
          selectedLocation={selectedLocation}
        />

        {/* Forecast result card */}
        <ForecastResult forecastData={forecastData} />
      </div>

      {/* FOOTER (Dark-themed) */}
      <Footer />
    </div>
  );
}

export default App;
