// frontend/src/components/ForecastForm.js
import React, { useState } from 'react';

function ForecastForm({ onGetForecast, onLocationSelect }) {
  const [cityName, setCityName] = useState('');
  const [forecastDate, setForecastDate] = useState('');

  // Forecast range now includes 1, 7, 14, 30
  const [forecastRange, setForecastRange] = useState(7);

  // Checkboxes for which parameters to include
  const [includeTemperature, setIncludeTemperature] = useState(true);
  const [includeWind, setIncludeWind] = useState(false);
  const [includeRain, setIncludeRain] = useState(false);
  const [includeMoisture, setIncludeMoisture] = useState(false);

  // Geocode a place name using the Nominatim API
  const geocodePlace = async () => {
    if (!cityName) {
      alert("Please enter a place name.");
      return;
    }
    try {
      const geocodeUrl = `https://nominatim.openstreetmap.org/search?q=${cityName}&format=json&limit=1`;
      const response = await fetch(geocodeUrl);
      const data = await response.json();

      if (data.length > 0) {
        const location = data[0];
        onLocationSelect(parseFloat(location.lat), parseFloat(location.lon));
      } else {
        alert("Location not found.");
      }
    } catch (err) {
      console.error("Geocoding error:", err);
      alert("Error geocoding location.");
    }
  };

  // Handler for the "Submit" button
  const handleSubmit = () => {
    if (!forecastDate) {
      alert("Please select a date for the forecast.");
      return;
    }

    // Build an object describing the user's selections
    const params = {
      forecastDate,
      forecastRange,
      temperature: includeTemperature,
      wind: includeWind,
      rain: includeRain,
      moisture: includeMoisture,
    };
    onGetForecast(params);
  };

  return (
    <div>
      <h2 style={{ marginBottom: '0.5rem', color: '#333' }}>Get Your Forecast</h2>
      <p style={{ fontSize: '0.9rem', color: '#666' }}>
        Choose a city or pick a spot on the map to get a detailed weather forecast.
      </p>

      {/* City Name + Geocode Button */}
      <label htmlFor="cityName">City Name</label>
      <input
        id="cityName"
        type="text"
        value={cityName}
        onChange={(e) => setCityName(e.target.value)}
        placeholder="e.g. London, Paris"
      />
      <button className="button" onClick={geocodePlace}>
        Find Location
      </button>

      {/* Forecast Date */}
      <label htmlFor="forecastDate">Specific Date</label>
      <input
        id="forecastDate"
        type="date"
        value={forecastDate}
        onChange={(e) => setForecastDate(e.target.value)}
      />

      {/* Forecast Range (now includes 1 day) */}
      <p>Forecast Range:</p>
      <label>
        <input
          type="radio"
          name="forecastRange"
          value={1}
          checked={forecastRange === 1}
          onChange={() => setForecastRange(1)}
        />
        1 Day
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="radio"
          name="forecastRange"
          value={7}
          checked={forecastRange === 7}
          onChange={() => setForecastRange(7)}
        />
        7 Days
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="radio"
          name="forecastRange"
          value={14}
          checked={forecastRange === 14}
          onChange={() => setForecastRange(14)}
        />
        14 Days
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="radio"
          name="forecastRange"
          value={30}
          checked={forecastRange === 30}
          onChange={() => setForecastRange(30)}
        />
        30 Days
      </label>

      {/* Checkboxes for parameters */}
      <p style={{ marginTop: '1rem' }}>Select Parameters:</p>
      <label>
        <input
          type="checkbox"
          checked={includeTemperature}
          onChange={() => setIncludeTemperature(!includeTemperature)}
        />
        Temperature
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="checkbox"
          checked={includeWind}
          onChange={() => setIncludeWind(!includeWind)}
        />
        Wind
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="checkbox"
          checked={includeRain}
          onChange={() => setIncludeRain(!includeRain)}
        />
        Rain
      </label>
      <label style={{ marginLeft: '1rem' }}>
        <input
          type="checkbox"
          checked={includeMoisture}
          onChange={() => setIncludeMoisture(!includeMoisture)}
        />
        Moisture
      </label>

      {/* Submit */}
      <button className="button" onClick={handleSubmit} style={{ marginTop: '1rem' }}>
        Submit
      </button>
    </div>
  );
}

export default ForecastForm;
