// frontend/src/components/MapComponent.js
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import React, { useEffect } from 'react';

function MapComponent({ onLocationSelect, selectedLocation }) {
  useEffect(() => {
    // Initialize the map
    const map = L.map('map').setView([51.505, -0.09], 2);

    // Load OSM tiles
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; OpenStreetMap contributors',
    }).addTo(map);

    // Add a default marker
    const marker = L.marker([51.505, -0.09]).addTo(map);

    // Map click handler
    function onMapClick(e) {
      const { lat, lng } = e.latlng;
      marker.setLatLng(e.latlng);
      onLocationSelect(lat, lng);
    }

    map.on('click', onMapClick);

    return () => {
      map.off('click', onMapClick);
      map.remove();
    };
  }, [onLocationSelect]);

  useEffect(() => {
    // If selectedLocation changes from city name geocode, update the map marker if needed.
    // This is left blank as a minimal example.
  }, [selectedLocation]);

  return (
    <div>
      <h2>Map</h2>
      <div id="map" style={{ height: "400px", width: "100%", margin: "10px 0" }}></div>
      <p>
        Selected Latitude: {selectedLocation.lat ? selectedLocation.lat.toFixed(5) : '-'}, 
        Longitude: {selectedLocation.lon ? selectedLocation.lon.toFixed(5) : '-'}
      </p>
    </div>
  );
}

export default MapComponent;
