// frontend/src/components/ForecastResult.js
import React from 'react';

function ForecastResult({ forecastData }) {
  if (!forecastData) {
    return (
      <div className="forecast-result">
        <h3>Forecast Overview</h3>
        <p style={{ color: '#777' }}>
          No forecast data yet. Select a location and click <strong>Submit</strong>.
        </p>
      </div>
    );
  }

  const {
    lat, lon,
    temperature,
    rainProbability,
    sunProbability,
    windSpeed,
    customization,
    forecastDate
  } = forecastData;

  return (
    <div className="forecast-result">
      <h3>Forecast Overview</h3>
      <div className="forecast-details">
        <img
          src="https://static.thenounproject.com/png/3271085-200.png"
          alt="Weather Icon"
          className="weather-icon"
        />
        <div>
          <p><strong>Coordinates:</strong> {lat.toFixed(5)}, {lon.toFixed(5)}</p>
          <p><strong>Temperature:</strong> {temperature} Â°C</p>
          <p><strong>Rain Probability:</strong> {rainProbability}%</p>
          <p><strong>Sun Probability:</strong> {sunProbability}%</p>
          <p><strong>Wind Speed:</strong> {windSpeed} km/h</p>
          {customization && <p><strong>Customization:</strong> {customization}</p>}
          {forecastDate && <p><strong>Specific Date:</strong> {forecastDate}</p>}
        </div>
      </div>

      {/* An alert section (just as an example) */}
      <div className="alert-section">
        <h3>Weather Alerts</h3>
        <p>No active alerts.</p>
      </div>
    </div>
  );
}

export default ForecastResult;
