// frontend/src/App.js
import React, { useState } from 'react';
import './App.css';

import NavBar from './components/NavBar';
import Footer from './components/Footer';

import MapComponent from './components/MapComponent';
import ForecastForm from './components/ForecastForm';
import ForecastResult from './components/ForecastResult';

function App() {
  const [selectedLocation, setSelectedLocation] = useState({ lat: null, lon: null });
  const [forecastData, setForecastData] = useState(null);

  // Called when the user selects a point on the map
  const handleLocationSelect = (lat, lon) => {
    setSelectedLocation({ lat, lon });
  };

  // Fetches the forecast from the Flask backend (port 8000 in this example)
  const getForecast = async (customization, date) => {
    if (!selectedLocation.lat || !selectedLocation.lon) {
      alert("Please select a location on the map or via city name first.");
      return;
    }

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/weather?lat=${selectedLocation.lat}&lon=${selectedLocation.lon}`
      );
      const data = await response.json();
      if (data.error) {
        alert(data.error);
      } else {
        const augmentedData = {
          ...data,
          customization,
          forecastDate: date,
        };
        setForecastData(augmentedData);
      }
    } catch (err) {
      console.error('Error fetching data from backend:', err);
      alert('Error fetching data from backend.');
    }
  };

  return (
    <div className="app-container">
      {/* NAV BAR */}
      <NavBar />

      <div className="container">
        {/* Forecast form card */}
        <div className="card forecast-form">
          <ForecastForm onGetForecast={getForecast} onLocationSelect={handleLocationSelect} />
        </div>

        {/* Map section card */}
        <div className="card map-section">
          <MapComponent 
            onLocationSelect={handleLocationSelect}
            selectedLocation={selectedLocation}
          />
        </div>

        {/* Forecast result card */}
        <div className="card">
          <ForecastResult forecastData={forecastData} />
        </div>
      </div>

      {/* FOOTER */}
      <Footer />
    </div>
  );
}

export default App;
